select * from customer
--1)	What is the total revenue generated by male Vs female customers?
select gender, sum(purchase_amount) as revenue
from customer
group by gender

--2)	Which customer used a discount but still spent more than the average purchase amount?
select customer_id, purchase_amount from customer
where customer.purchase_amount > (select avg(purchase_amount) from customer) and discount_applied = 'Yes'

--3)	Which are the top 5 products with the highest average review rating?

select item_purchased,category,ROUND(avg(review_rating::numeric),2) as avg_rating from customer
group by customer.item_purchased, customer.category
order by avg(review_rating) desc
limit 5

--select item_purchased,category,review_rating from customer
